import React from 'react';

class GoogleAuth extends React.Component {

  state = {isSignedIn: null};


  componentDidMount() {
    window.gapi.load('client:auth2', () => {
      window.gapi.auth2.init({
        clientId: '500899028401-7dlh1v2nfvgkfrigk87f4fqjepnrn98c.apps.googleusercontent.com',
        scpoe: 'email'
      }).then(() => {
        this.setState({ isSignedIn: window.gapi.auth2.getAuthInstance().isSignedIn.get() });
        window.gapi.auth2.getAuthInstance().isSignedIn.listen(this.onAuthChange);
      });
    });
  }

  onAuthChange = () => {
    this.setState({ isSignedIn: window.gapi.auth2.getAuthInstance().isSignedIn.get() });
  }

  renderAuthButton() {
    console.log('isisgnedin ', this.state.isSignedIn)
    if(this.state.isSignedIn === null) {
      return <div>I dont know if signed</div>;
    } else if(this.state.isSignedIn) {
      return <div>SIgned in!</div>;
    } else {
      return <div>Not signed</div>;
    }
  }


  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

export default GoogleAuth;
